import { Component } from '@angular/core';
import { NotesService } from './notes.service';
@Component({
    selector: 'notes-component',
    templateUrl: './notes.component.html',
    styleUrls: []
})
export class NotesComponent {
    notes$: any = [];

    constructor(private NotesService: NotesService) { }

    ngOnInit() {
        this.GetData()

    }
    setValue(name: string, text: string) {

        this.NotesService.addNote(name, text).subscribe()
        setTimeout(() => {
            this.GetData()
        }, 100);
    }
    GetData() {
        this.NotesService.getNote().subscribe(data => { this.notes$ = data })
    }

    OnDelete(note: any) {
        this.NotesService.deleteNote(note).subscribe(data: any => { console.log(data) })
        setTimeout(() => {
            this.GetData()
        }, 100);

    }
    OnChange(note: any) {
        note.name = note.name + "a"
        this.NotesService.changeNote(note).subscribe()


    }
}
