import { Component } from '@angular/core';
import { NotesService } from './notes.service';
@Component({
    selector: 'notes-component',
    templateUrl: './notes.component.html',
    styleUrls: []
})
export class NotesComponent {
    notes: any = [{ name: "cau", text: "hau" }];

    constructor(private NoteService: NotesService) { }

    ngOnInit() {
        this.GetData()

    }
    setValue(name: string, text: string) {

        this.NoteService.addNote(name, text).subscribe()
        setTimeout(() => {
            this.GetData()
        }, 100);
    }
    GetData() {
        this.NoteService.getNote().subscribe(data => { this.notes = data })
    }

    OnDelete(note: any) {
        this.NoteService.deleteNote(note).subscribe()
        setTimeout(() => {
            this.GetData()
        }, 100);

    }
    OnChange(note: any) {
        note.name = note.name + "a"
        this.NoteService.changeNote(note).subscribe()


    }
}
