import { Component } from '@angular/core';
import { NotesService } from './notes.service';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Rest Notes';
  notes$: any = [];
  first_name: string = "";
  constructor(private NotesService: NotesService) { }

  ngOnInit() {
    this.GetData()

  }
  setValue(first_name: string) {
    console.log('First Name: ', first_name);
    this.first_name = ""
  }
  GetData() {
    this.NotesService.getNote().subscribe(data => { this.notes$ = data })
  }
  OnSubmit() {
    this.NotesService.addNote().subscribe()
  }
  OnDelete(note: any) {
    this.NotesService.deleteNote(note).subscribe(data => { console.log(data) })
    setTimeout(() => {
      this.GetData()
    }, 100);

  }
  OnChange(note: any) {
    note.name = note.name + "a"
    this.NotesService.changeNote(note).subscribe()


  }



}
