import { Component } from '@angular/core';
import { NotesService } from './notes.service';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Rest Notes';
  notes$: any = [];

  constructor(private NotesService: NotesService) { }

  ngOnInit() {
    this.GetData()

  }
  setValue(name: any, text: any) {
    console.log('First Name: ', name);
    this.NotesService.addNote(name, text).subscribe()
  }
  GetData() {
    this.NotesService.getNote().subscribe(data => { this.notes$ = data })
  }
  OnSubmit() {

  }
  OnDelete(note: any) {
    this.NotesService.deleteNote(note).subscribe(data => { console.log(data) })
    setTimeout(() => {
      this.GetData()
    }, 100);

  }
  OnChange(note: any) {
    note.name = note.name + "a"
    this.NotesService.changeNote(note).subscribe()


  }



}
