import { Injectable } from '@angular/core'
import { HttpClient } from '@angular/common/http'

class Note {
    id: number;
    name: string;
    text: string;
}
@Injectable({ providedIn: 'root' })

export class NotesService {
    text: String = '';
    name: String = '';

    constructor(private http: HttpClient) { }

    getNote() {
        return this.http.get('http://127.0.0.1:8000/api/notes/')
    }
    addNote() {

        return this.http.post('http://127.0.0.1:8000/api/notes/create/', { name: "name", text: "text" });
    }
    deleteNote(note: any) {

        return this.http.delete('http://127.0.0.1:8000/api/notes/' + note.id + '/delete/');
    }
    changeNote(note: any) {
        this.name = note.name
        return this.http.delete('http://127.0.0.1:8000/api/notes/' + note.id + '/update/', { name: this.name, text: note.text });
    }
}
